INSERT INTO "mailmerge_template" VALUES ('shell script for envelopes','read -p "Next..."\015\012/bin/echo -e "<sender/>\\r" > /dev/lp0\015\012forEachSenderAddressLine("\015\012/bin/echo -e "<line/>\\r" > /dev/lp0")\015\012/bin/echo -e "\\r\\n\\n\\n\\n\\n\\n\\n\\n\\n" > /dev/lp0\015\012/bin/echo -e "                              <householdNames/>\\r" > /dev/lp0\015\012forEachAddressLine("\015\012/bin/echo -e "                              <line/>\\r" > /dev/lp0")\015\012/bin/echo -e "\\r\\n\\n\\n\\n\\n\\n\\n\\n\\n\\n\\n" > /dev/lp0\015\012',1);
INSERT INTO "mailmerge_template" VALUES ('postscript envelope','%!                    %  header goes here\015\012(Times) findfont [10 0 0 10 0 0] makefont setfont\015\012176 155 moveto \015\012gsave 90 rotate \015\012\011(<sender/>) show \015\012grestore\015\012forEachSenderAddressLine("\015\012188 12 <lineNumber/> mul add 155 moveto gsave 90 rotate (<line/>) show grestore")\015\012% destination address\015\012(Times) findfont [14 0 0 14 0 0] makefont setfont\015\012300 400 moveto gsave 90 rotate (<otherNames/> <lastName/>) show grestore\015\012forEachAddressLine("\015\012316 16 <lineNumber/> mul add 400 moveto gsave 90 rotate (<line/>) show grestore")\015\012showpage              %  finish job\015\012',1);
